SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_liberacao (nr_seq_precicao_p bigint, nm_usuario_p text, ie_tipo_release_p text) AS $body$
BEGIN

if (ie_tipo_release_p = 'A')then

	update PE_PRESCRICAO
	set dt_liberacao = clock_timestamp(),
	nm_usuario_nrec = nm_usuario_p
	where nr_sequencia = nr_seq_precicao_p;

end if;

if (ie_tipo_release_p = 'B')then

	update PE_PRESCR_CP_PROGRESS
	set dt_liberacao = clock_timestamp(),
	nm_usuario_nrec = nm_usuario_p
	where nr_sequencia = nr_seq_precicao_p;
	
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
