SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_lib_com_cliente_hist ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_cliente_w	bigint;
ie_liberado_w		varchar(1) := 'N';


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_liberado_w
	from 	com_cliente_hist
	where	nr_sequencia = nr_sequencia_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

	if (ie_liberado_w = 'S') then
		begin

		update	com_cliente_hist
		set 	dt_liberacao	 = NULL,
			nm_usuario	= nm_usuario_p,
			nm_usuario_lib	 = NULL
		where	nr_sequencia	= nr_sequencia_p;

		end;
	else if (ie_liberado_w = 'N') then
		begin

		select	max(nr_seq_cliente)
		into STRICT	nr_seq_cliente_w
		from	com_cliente_hist
		where	nr_sequencia 	= nr_sequencia_p;

		if (nr_seq_cliente_w IS NOT NULL AND nr_seq_cliente_w::text <> '') then
			begin

			update	com_cliente
			set	ie_revisado 	= 'N',
				nm_usuario	= nm_usuario_p
			where	nr_sequencia	= nr_seq_cliente_w;

			update	com_cliente_hist
			set 	dt_liberacao	= clock_timestamp(),
				nm_usuario	= nm_usuario_p,
				nm_usuario_lib	= nm_usuario_p
			where	nr_sequencia	= nr_sequencia_p;

			end;
		end if;
		end;
	end if;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
