CREATE OR REPLACE PROCEDURE atualizar_lote_contabil_export ( nr_lote_contabil_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (nr_lote_contabil_p IS NOT NULL AND nr_lote_contabil_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	update	lote_contabil
	set	ie_exportado 	= 'S',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_lote_contabil = nr_lote_contabil_p;
	end;
end if;

commit;
end;
$body$
