CREATE OR REPLACE PROCEDURE atualizar_lote_laudo_sus ( nr_sequencia_p bigint, ie_opcao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*
ie_opcao_p
 1 - Envio
 2 - Retorno
*/
BEGIN

if (ie_opcao_p	= 1) then
	begin
	update	sus_lote_autor
	set	dt_envio		= clock_timestamp(),
		nm_usuario_envio	= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_sequencia_p;
	end;
elsif (ie_opcao_p	= 2) then
	begin
	update	sus_lote_autor
	set	dt_retorno		= clock_timestamp(),
		nm_usuario_retorno	= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_sequencia_p;
	end;
elsif (ie_opcao_p	= 3) then
	begin
	update	sus_lote_autor
	set	dt_envio		 = NULL,
		nm_usuario_envio	 = NULL,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_sequencia_p;
	end;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
