CREATE OR REPLACE PROCEDURE atualizar_marcacao_portal (cd_agenda_p bigint, cd_perfil_p bigint) AS $body$
DECLARE


ie_apres_agenda_externo_w agenda.ie_apres_agenda_externo%type;

C01 CURSOR FOR
  SELECT cd_agenda, ie_apres_agenda_externo
  from agenda
  where ie_situacao = 'A'
  and cd_agenda <> cd_agenda_p
  and cd_tipo_agenda in (3,4,5)
  and cd_agenda in (SELECT distinct cd_agenda
                    from med_permissao
                    where cd_perfil  = cd_perfil_p)

union

  select cd_agenda, ie_apres_agenda_externo
  from agenda
  where ie_situacao = 'A'
  and cd_agenda <> cd_agenda_p
  and cd_tipo_agenda = 2
  and cd_agenda in (select distinct cd_agenda
                    from agenda_regra_permissao
                    where cd_perfil  = cd_perfil_p);

BEGIN

select max(ie_apres_agenda_externo)
into STRICT ie_apres_agenda_externo_w
from agenda
where cd_agenda = cd_agenda_p;

if (ie_apres_agenda_externo_w IS NOT NULL AND ie_apres_agenda_externo_w::text <> '') then

  for c_01 in C01 loop

    if coalesce(c_01.ie_apres_agenda_externo::text, '') = '' or c_01.ie_apres_agenda_externo <> ie_apres_agenda_externo_w then
      update agenda
      set ie_apres_agenda_externo = ie_apres_agenda_externo_w
      where cd_agenda = c_01.cd_agenda;

    end if;

  end loop;

  commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
