SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_material_autorizado (nr_prescricao_p bigint, nr_seq_prescricao_p bigint, cd_material_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_mat_autor_w		bigint;
cd_material_w			bigint;

BEGIN

select	coalesce(max(nr_sequencia),0),
	coalesce(max(cd_material),0)
into STRICT	nr_seq_mat_autor_w,
	cd_material_w
from	material_autorizado
where	nr_prescricao = nr_prescricao_p
and	nr_seq_prescricao = nr_seq_prescricao_p
and	cd_material <> cd_material_p;

if (coalesce(cd_material_w,0) <> 0) then

	update	material_autorizado
	set	cd_material = cd_material_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		ds_observacao   = substr(ds_observacao || chr(10)||chr(13)|| wheb_mensagem_pck.get_texto(311544,'CD_MATERIAL_W='||cd_material_w||';CD_MATERIAL_P='||cd_material_p),1,2000)
	where	nr_sequencia = nr_seq_mat_autor_w;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
