CREATE OR REPLACE PROCEDURE atualizar_material_exame ( nr_sequencias_p text, cd_material_exame_p text, nr_prescricao_p bigint, cd_perfil_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencias_w		varchar(2000);
nr_pos_virgula_w	bigint;
nr_sequencia_w		integer;
ds_erro_w		varchar(255);

BEGIN
 
if (nr_sequencias_p IS NOT NULL AND nr_sequencias_p::text <> '') then 
begin 
	nr_sequencias_w	:=	nr_sequencias_p;
	while(nr_sequencias_w IS NOT NULL AND nr_sequencias_w::text <> '') loop 
	begin 
		nr_pos_virgula_w	:= position(',' in nr_sequencias_w);
		if (nr_pos_virgula_w > 0) then 
		begin 
			nr_sequencia_w	:= (substr(nr_sequencias_w, 1, nr_pos_virgula_w-1))::numeric;
			nr_sequencias_w	:= substr(nr_sequencias_w, nr_pos_virgula_w+1, length(nr_sequencias_w));
			if (nr_sequencia_w > 0) then 
			begin 
				update	prescr_procedimento  
				set 	cd_material_exame 	= cd_material_exame_p 
				where 	nr_prescricao 		= nr_prescricao_p 
				and 	nr_sequencia 		= nr_sequencia_w;
				 
				ds_erro_w := Consistir_prescr_procedimento(nr_prescricao_p, nr_sequencia_w, nm_usuario_p, cd_perfil_p, ds_erro_w);
			end;
			end if;
		end;
		end if;
	end;
	end loop;
end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
