SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_medico_convenio ( cd_convenio_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



cd_pessoa_fisica_w	varchar(20);

c01 CURSOR FOR
SELECT	b.cd_pessoa_fisica
from	medico b
where	ie_corpo_clinico = 'S'
and 	not exists (select x.cd_pessoa_fisica from medico_convenio x
		   where x.cd_pessoa_fisica = b.cd_pessoa_fisica
		   and x.cd_convenio = cd_convenio_p);

BEGIN

open 	c01;
loop
fetch c01 	into
		cd_pessoa_fisica_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		insert into medico_convenio(NR_SEQUENCIA,
						CD_PESSOA_FISICA,
						CD_CONVENIO,
						DT_ATUALIZACAO,
						NM_USUARIO,
						IE_CONVENIADO,
						IE_AUDITOR,
						CD_MEDICO_CONVENIO,
						IE_PLANTONISTA,
						cd_estabelecimento,
						IE_TIPO_SERVICO_SUS
						)
		SELECT	nextval('medico_convenio_seq'),
			b.cd_pessoa_fisica,
                        cd_convenio_p,
                        clock_timestamp(),
			nm_usuario_p,
			'S',
			'N',
			null,
			'N',
			cd_estabelecimento_p,
			null
		from 	medico b
		where 	ie_corpo_clinico = 'S'
		and 	not 	exists (SELECT x.cd_pessoa_fisica
			from 	medico_convenio x
			where 	x.cd_pessoa_fisica = b.cd_pessoa_fisica
			and 	x.cd_convenio = cd_convenio_p);
		end;
end loop;
close c01;
commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
