CREATE OR REPLACE PROCEDURE atualizar_medico_conv_periodo (cd_estabelecimento_p bigint, cd_medico_p text, cd_convenio_p bigint, ie_status_conta_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_seq_proc_w		bigint;
nr_interno_conta_w		bigint;
cd_medico_conv_w	varchar(10);
cd_medico_anterior_w	varchar(15);

 
c01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.nr_interno_conta, 
		Obter_Medico_Convenio(b.cd_estabelecimento, a.cd_medico_executor, b.cd_convenio_parametro, null, null, null, 
		a.cd_setor_atendimento,a.dt_procedimento, null, a.ie_funcao_medico, null) cd_medico_conv, 
		a.cd_medico_convenio 
	from	conta_paciente b, 
		procedimento_paciente a 
	where	a.nr_interno_conta	= b.nr_interno_conta 
	and	a.cd_medico_executor	= cd_medico_p 
	and	((ie_status_conta_p = 3) or (b.ie_status_acerto = ie_status_conta_p)) 
	and	b.cd_convenio_parametro	= cd_convenio_p 
	and	b.dt_mesano_referencia	between dt_inicial_p and dt_final_p 
	and	coalesce(b.nr_seq_protocolo::text, '') = '';


BEGIN 
 
open	c01;
loop 
fetch	c01 into 
	nr_seq_proc_w, 
	nr_interno_conta_w, 
	cd_medico_conv_w, 
	cd_medico_anterior_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
 
		update	procedimento_paciente 
		set	cd_medico_convenio	= cd_medico_conv_w, 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao		= clock_timestamp() 
		where	nr_sequencia		= nr_seq_proc_w;		
 
		end;
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
