CREATE OR REPLACE PROCEDURE atualizar_medico_prescritor ( ds_lista_p text, cd_medico_prescritor_p text, nr_seq_material_p bigint ) AS $body$
BEGIN

if (coalesce(nr_seq_material_p,0) > 0) then
	update	material_atend_paciente
	set	cd_medico_prescritor = cd_medico_prescritor_p
	where	nr_sequencia = nr_seq_material_p;
else
	update	material_atend_paciente
	set	cd_medico_prescritor = cd_medico_prescritor_p
	where   obter_se_contido(nr_sequencia, ds_lista_p) = 'S';
end if;

commit;

end;
$body$
