CREATE OR REPLACE PROCEDURE atualizar_medico_prescr_exame (nr_prescricao_p bigint, cd_medico_p text, dt_prescricao_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
cd_setor_atendimento_w		integer;
dt_liberacao_prescr_w		timestamp;

BEGIN
select	max(dt_liberacao) 
into STRICT	dt_liberacao_prescr_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_p;
 
if (dt_liberacao_prescr_w IS NOT NULL AND dt_liberacao_prescr_w::text <> '') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(184196);
end if;
 
select	coalesce(max(a.cd_setor_atendimento),0) 
into STRICT	cd_setor_atendimento_w 
from	prescr_medica b, 
	atendimento_paciente_v a 
where	a.nr_atendimento	= b.nr_atendimento 
and	b.nr_prescricao		= nr_prescricao_p;
 
if (cd_setor_atendimento_w IS NOT NULL AND cd_setor_atendimento_w::text <> '') and (cd_setor_atendimento_w <> 0) then 
	begin 
	 
	update	prescr_medica 
	set	cd_medico		= cd_medico_p, 
		cd_setor_atendimento	= cd_setor_atendimento_w, 
		dt_prescricao		= dt_prescricao_p, 
		dt_atualizacao		= clock_timestamp(), 
		nm_usuario		= nm_usuario_p 
	where	nr_prescricao		= nr_prescricao_p;	
 
	end;
else 
	update	prescr_medica 
	set	cd_medico		= cd_medico_p, 
		dt_prescricao		= dt_prescricao_p, 
		dt_atualizacao		= clock_timestamp(), 
		nm_usuario		= nm_usuario_p 
	where	nr_prescricao		= nr_prescricao_p;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
