CREATE OR REPLACE PROCEDURE atualizar_medic_prot_composto ( cd_protocolo_p bigint, nr_sequencia_p bigint, nr_seq_material_p bigint, nr_agrupamento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_dias_aplicacao_w		varchar(4000);
nr_agrupamento_w		double precision;
ds_recomendacao_w		varchar(4000);
ie_via_aplicacao_w		varchar(5);
cd_intervalo_w		varchar(7);
ds_horarios_w			varchar(2000);
ie_se_necessario_w		varchar(1);
qt_minuto_aplicacao_w	smallint;
ie_bomba_infusao_w		varchar(1);
ie_aplic_bolus_w		varchar(1);
ie_aplic_lenta_w		varchar(1);
ie_acm_w			varchar(1);
hr_prim_horario_w		varchar(5);
ie_urgencia_w			varchar(1);
qt_hora_aplicacao_w		smallint;
qt_solucao_w			double precision;
cd_protocolo_w		bigint;
nr_sequencia_w		integer;
nr_seq_material_w		integer;


C01 CURSOR FOR
SELECT	cd_protocolo,
	nr_sequencia,
	nr_seq_material
from	protocolo_medic_material
where	cd_protocolo	= cd_protocolo_p
and	nr_sequencia	= nr_sequencia_p
and	nr_seq_material <> nr_seq_material_p
and	nr_agrupamento	= nr_agrupamento_p
and	ie_agrupador	= 1
order by nr_seq_material desc;


BEGIN

select	ds_dias_aplicacao,
	nr_agrupamento,
	ds_recomendacao,
	ie_via_aplicacao,
	cd_intervalo,
	ds_horarios,
	ie_se_necessario,
	qt_minuto_aplicacao,
	ie_bomba_infusao,
	ie_aplic_bolus,
	ie_aplic_lenta,
	ie_acm,
	hr_prim_horario,
	ie_urgencia,
	qt_hora_aplicacao,
	qt_solucao
into STRICT	ds_dias_aplicacao_w,
	nr_agrupamento_w,
	ds_recomendacao_w,
	ie_via_aplicacao_w,
	cd_intervalo_w,
	ds_horarios_w,
	ie_se_necessario_w,
	qt_minuto_aplicacao_w,
	ie_bomba_infusao_w,
	ie_aplic_bolus_w,
	ie_aplic_lenta_w,
	ie_acm_w,
	hr_prim_horario_w,
	ie_urgencia_w,
	qt_hora_aplicacao_w,
	qt_solucao_w
from	protocolo_medic_material
where	cd_protocolo	= cd_protocolo_p
and	nr_sequencia	= nr_sequencia_p
and	nr_seq_material = nr_seq_material_p;
	
OPEN C01;
LOOP
FETCH C01 into
	cd_protocolo_w,
	nr_sequencia_w,
	nr_seq_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	update	protocolo_medic_material
	set	ds_dias_aplicacao	= ds_dias_aplicacao_w,
		nr_agrupamento		= nr_agrupamento_w,
		ds_recomendacao		= ds_recomendacao_w,
		ie_via_aplicacao	= ie_via_aplicacao_w,
		cd_intervalo		= cd_intervalo_w,
		ds_horarios		= ds_horarios_w,
		ie_se_necessario	= ie_se_necessario_w,
		qt_minuto_aplicacao	= qt_minuto_aplicacao_w,
		ie_bomba_infusao	= ie_bomba_infusao_w,
		ie_aplic_bolus		= ie_aplic_bolus_w,
		ie_aplic_lenta		= ie_aplic_lenta_w,
		ie_acm			= ie_acm_w,
		hr_prim_horario		= hr_prim_horario_w,
		ie_urgencia		= ie_urgencia_w,
		qt_hora_aplicacao	= qt_hora_aplicacao_w,
		qt_solucao		= qt_solucao_w
	where	cd_protocolo		= cd_protocolo_p
	and	nr_sequencia		= nr_sequencia_p
	and	nr_seq_material		= nr_seq_material_w;
	
END LOOP;
CLOSE C01;

commit;

END;
$body$
