CREATE OR REPLACE PROCEDURE atualizar_mesano_protocolo_js ( nr_interno_conta_p bigint, nr_protocolo_p text, nr_seq_protocolo_p bigint, dt_mesano_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
qt_fatur_etapa_w	bigint;
				

BEGIN 
 
if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '')then 
	begin 
	 
	update 	conta_paciente 
	set  	dt_atualizacao 		= clock_timestamp() , 
		nm_usuario 		= nm_usuario_p, 
		nr_protocolo 		= nr_protocolo_p, 
		nr_seq_protocolo 	= nr_seq_protocolo_p, 
		dt_mesano_referencia 	= dt_mesano_referencia_p 
	where 	nr_interno_conta 	= nr_interno_conta_p;
	 
	select 	count(*) 
	into STRICT	qt_fatur_etapa_w 
	from 	fatur_etapa_alta 
	where 	ie_evento = 'B';
	 
	if (qt_fatur_etapa_w > 0)then 
		begin 
		 
		CALL gerar_etapa_inserir_protocolo(nr_interno_conta_p, nm_usuario_p);
		 
		end;
	end if;
	 
	end;
end if;
 
commit;
 
end;
$body$
