CREATE OR REPLACE PROCEDURE atualizar_min_aprovadores ( cd_processo_aprov_p bigint, nr_nivel_aprovacao_p bigint, qt_minimo_aprovador_p bigint) AS $body$
BEGIN

if (coalesce(qt_minimo_aprovador_p,0) < 1) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265574);
	--'A quantidade mínima de aprovadores não pode ser menor que 1.');
end if;

update	processo_aprov_resp
set	qt_minimo_aprovador = qt_minimo_aprovador_p
where	cd_processo_aprov = cd_processo_aprov_p
and	nr_nivel_aprovacao = nr_nivel_aprovacao_p;

commit;

end;
