SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_motivo_alta (nr_atendimento_p bigint, cd_motivo_alta_p text) AS $body$
DECLARE

 
nr_seq_atend_alta_w	bigint;
			

BEGIN 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_motivo_alta_p IS NOT NULL AND cd_motivo_alta_p::text <> '') then 
	 
	update	atendimento_paciente 
	set	cd_motivo_alta = cd_motivo_alta_p 
	where	nr_atendimento = nr_atendimento_p;
	 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_atend_alta_w 
	from	atend_alta_hist 
	where	nr_atendimento = nr_atendimento_p 
	and	ie_alta_estorno = 'A';
	 
	if (coalesce(nr_seq_atend_alta_w,0) > 0) then 
		update	atend_alta_hist 
		set	cd_motivo_alta	= cd_motivo_alta_p, 
			nm_usuario	= wheb_usuario_pck.get_nm_usuario, 
			dt_atualizacao	= clock_timestamp() 
		where	nr_sequencia	= nr_seq_atend_alta_w;
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
