SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_necess_aut_qt ( nr_seq_pendencia_p bigint, cd_estabelecimento_p bigint, dt_agenda_p timestamp, nm_usuario_p text ) AS $body$
DECLARE


nr_seq_atendimento_w bigint;
nr_seq_paciente_w bigint;
nr_sequencia_w bigint;
nr_ciclo_w bigint;
ie_estagio_autorizacao_w varchar(20);


BEGIN

ie_estagio_autorizacao_w := obter_param_usuario(865, 264, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_estagio_autorizacao_w);


if (ie_estagio_autorizacao_w IS NOT NULL AND ie_estagio_autorizacao_w::text <> '') then

select 	nr_seq_atendimento,
		nr_seq_paciente,
		nr_ciclo
into STRICT
		nr_seq_atendimento_w,
		nr_seq_paciente_w,
		nr_ciclo_w
from	paciente_atendimento a
where	a.nr_Seq_pend_agenda	= nr_seq_pendencia_p;

select coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	autorizacao_convenio
where	nr_seq_paciente = nr_seq_atendimento_w
and 	((coalesce(DT_INICIO_VIGENCIA, dt_agenda_p) > dt_agenda_p) or (coalesce(DT_FIM_VIGENCIA, dt_agenda_p) < dt_agenda_p));

if (nr_sequencia_w = 0) then
	select coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	autorizacao_convenio
	where	nr_seq_paciente_setor = nr_seq_paciente_w
	and 	nr_ciclo = nr_ciclo_w
	and 	((coalesce(DT_INICIO_VIGENCIA, dt_agenda_p) > dt_agenda_p) or (coalesce(DT_FIM_VIGENCIA, dt_agenda_p) < dt_agenda_p));
end if;

if (nr_sequencia_w <> 0) then
	update	autorizacao_convenio
	set	dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		nr_seq_estagio	= ie_estagio_autorizacao_w
	where	nr_sequencia	= nr_sequencia_w;
end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
