CREATE OR REPLACE PROCEDURE atualizar_nf_facturador ( ds_selo_digital_emissor_p text, ds_selo_certif_digital_sat_p text, ds_serie_certif_contr_p text, ds_serie_certif_sat_p text, nr_nfe_imp_p text, nr_seq_nota_fiscal_p text) AS $body$
BEGIN

if (nr_seq_nota_fiscal_p IS NOT NULL AND nr_seq_nota_fiscal_p::text <> '') then
