		ds_selo_certif_digital_sat = ds_selo_certif_digital_sat_p,
		ds_serie_certif_contr = ds_serie_certif_contr_p,
		ds_serie_certif_sat = ds_serie_certif_sat_p,
		nr_nfe_imp = nr_nfe_imp_p,
		dt_emissao_nfe = clock_timestamp()
	where	nr_sequencia = nr_seq_nota_fiscal_p;
	commit;
	end;
end if;

end;
