CREATE OR REPLACE PROCEDURE atualizar_nr_atual_reg_audit ( nr_seq_lote_audit_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_lote_w	bigint;


BEGIN
if (nr_seq_lote_audit_p IS NOT NULL AND nr_seq_lote_audit_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
	begin

	select	count(*)
	into STRICT	qt_lote_w
	from	lote_auditoria
	where	nr_sequencia = nr_seq_lote_audit_p;

	if (qt_lote_w <> 0) then
		begin

		update   regra_num_reg_audit
		set      nr_atual             = nr_atual + 1
		where    cd_estabelecimento   = cd_estabelecimento_p;

		commit;
		end;
	end if;
	end;
end if;

end;
