CREATE OR REPLACE PROCEDURE atualizar_nr_exame_ge ( nr_exame_p text, nr_seq_proced_p bigint, nr_prescricao_p bigint, cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_consistir_w    varchar(1);
ie_atualizar_exame_w  varchar(1);
ie_continuar_w    integer := 0;


BEGIN

ie_consistir_w := obter_param_usuario(942, 240, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_consistir_w);
ie_atualizar_exame_w := obter_param_usuario(942, 237, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_atualizar_exame_w);

if (ie_consistir_w = 'S'
  and (nr_exame_p IS NOT NULL AND nr_exame_p::text <> '')) then

  select   count(*)
  into STRICT  ie_continuar_w
  from   prescr_procedimento
  where   nr_seq_lab = nr_exame_p;

  if (ie_continuar_w > 0) then
    CALL wheb_mensagem_pck.exibir_mensagem_abort(818014);
  end if;

end if;

--Usando na Gest√£o de exames Java
update   prescr_procedimento
set   nr_seq_lab = nr_exame_p,
  nm_usuario = nm_usuario_p,
  dt_atualizacao = clock_timestamp()
where   nr_prescricao = nr_prescricao_p
and   nr_sequencia = nr_seq_proced_p;

if (ie_atualizar_exame_w = 'S') then
  CALL alterar_nr_exames_js(nr_prescricao_p, nr_seq_proced_p, nr_exame_p, nm_usuario_p);
end if;

commit;

end;
