CREATE OR REPLACE PROCEDURE atualizar_nr_seq_status_pato ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_status_pato_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_status_pat_ant_w		bigint;


BEGIN
if (nr_seq_status_pato_p IS NOT NULL AND nr_seq_status_pato_p::text <> '') then
	begin

	select	nr_seq_status_pato
	into STRICT	nr_seq_status_pat_ant_w
	from   	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	and		nr_sequencia  = nr_seq_prescr_p;

	update	prescr_procedimento
	set		nr_seq_status_pato = nr_seq_status_pato_p,
			dt_atualizacao	   = clock_timestamp(),
			nm_usuario		   = nm_usuario_p
	where	nr_prescricao = nr_prescricao_p
	and		nr_sequencia  = nr_seq_prescr_p;

	CALL gravar_log_cdi(34345, obter_desc_expressao(325814) || nr_prescricao_p || obter_desc_expressao(306401)||': ' || nr_seq_prescr_p  || obter_desc_expressao(341002) || nr_seq_status_pat_ant_w || obter_desc_expressao(341003) || nr_seq_status_pato_p,nm_usuario_p);

	commit;

	end;
end if;

end;
$body$
