CREATE OR REPLACE PROCEDURE atualizar_observacao_transacao ( nr_seq_caixa_rec_p bigint, ds_observacao_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;

C01 CURSOR FOR
SELECT	nr_sequencia
from	movto_trans_financ
where	nr_seq_caixa_rec = nr_seq_caixa_rec_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	movto_trans_financ
	set	ds_observacao = substr(ds_observacao ||chr(13)||chr(10)|| ds_observacao_p,1,4000)
	where	nr_sequencia = nr_sequencia_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
