SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_obs_exames ( ds_observacao_p text, nm_usuario_p text, ds_lista_agendas_p text) AS $body$
DECLARE


nr_seq_agenda_w					agenda_paciente.nr_sequencia%type;
ds_lista_agendas_w				varchar(4000);
ie_pos_virgula_w					smallint;
tam_lista_w					bigint;
								

BEGIN

if (ds_lista_agendas_p IS NOT NULL AND ds_lista_agendas_p::text <> '') and (ds_observacao_p IS NOT NULL AND ds_observacao_p::text <> '') then

    ds_lista_agendas_w := ds_lista_agendas_p;

    while (ds_lista_agendas_w IS NOT NULL AND ds_lista_agendas_w::text <> '') loop
		
		ie_pos_virgula_w	:= position(',' in ds_lista_agendas_w);
		tam_lista_w	:= length(ds_lista_agendas_w);

		if (ie_pos_virgula_w <> 0) then
			nr_seq_agenda_w			:= substr(ds_lista_agendas_w,1,(ie_pos_virgula_w - 1));
		else
			nr_seq_agenda_w			:= ds_lista_agendas_w;
		end if;
		
		ds_lista_agendas_w	:= substr(ds_lista_agendas_w,(ie_pos_virgula_w + 1),tam_lista_w);
		
		update  agenda_paciente
		set	ds_observacao = ds_observacao_p,
			nm_usuario   = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia = nr_seq_agenda_w;
		
	end loop;

	commit;		
	
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
