CREATE OR REPLACE PROCEDURE atualizar_obs_farm_html5 ( ds_observacao_p text, nr_prescricao_p prescr_medica.nr_prescricao%type, nr_seq_cpoe_p cpoe_material.nr_sequencia%type, nr_seq_obs_p bigint, ie_gravar_obs_prescr_p text) AS $body$
DECLARE


nr_prescricao_vigente_w		prescr_medica.nr_prescricao%type;


BEGIN

nr_prescricao_vigente_w := gpt_obter_prescricao_vigente(nr_seq_cpoe_p, 'M');

update	cpoe_material a
set		a.ds_observacao_far = ds_observacao_p,
		a.nr_seq_obs = nr_seq_obs_p,
		a.ds_observacao = CASE WHEN ie_gravar_obs_prescr_p='S' THEN  substr(a.ds_observacao,1,1900) || chr(13) || obter_desc_expressao(728544) || ' ' || ds_observacao_p, a.ds_observacao)		-- 728544: 'Observacao farmacia:'where	a.nr_sequencia = nr_seq_cpoe_p;update	prescr_material aset		a.ds_observacao_far = ds_observacao_p WHEN ie_gravar_obs_prescr_p=a.nr_seq_obs = nr_seq_obs_p THEN		a.ds_observacao = CASE WHEN ie_gravar_obs_prescr_p='S' THEN  substr(a.ds_observacao,1,1900) || chr(13) || obter_desc_expressao(728544) || ' ' || ds_observacao_p, a.ds_observacao)		-- 728544: 'Observacao farmacia:'where	a.nr_prescricao >= nr_prescricao_vigente_wand		a.nr_seq_mat_cpoe = nr_seq_cpoe_p;commit;end; END  END;
$body$
