CREATE OR REPLACE PROCEDURE atualizar_obs_medicamentos_rep ( nr_prescricao_p bigint, nr_sequencia_p bigint, nr_seq_obs_p bigint default 0) AS $body$
DECLARE


expressao1_w	varchar(255) := obter_desc_expressao_idioma(728544, null, wheb_usuario_pck.get_nr_seq_idioma);


BEGIN

update	prescr_material
set	ds_observacao = substr(ds_observacao,1,1900) || chr(13) || expressao1_w || ' ' || ds_observacao_far,
	nr_seq_obs	  = nr_seq_obs_p
where	nr_prescricao = nr_prescricao_p
and	nr_sequencia  = nr_sequencia_p;

end;
$body$
