CREATE OR REPLACE PROCEDURE atualizar_obs_pre_natal ( nr_seq_anamnese_p bigint, nr_seq_observacao_p bigint, nm_usuario_p text, ie_opcao_p text default null) AS $body$
BEGIN

if (nr_seq_anamnese_p IS NOT NULL AND nr_seq_anamnese_p::text <> '' AND nr_seq_observacao_p IS NOT NULL AND nr_seq_observacao_p::text <> '') then

	if (ie_opcao_p	= 'EVOL') then

		update 	MED_PAC_PRE_NATAL_EVOL
		set 	NR_SEQ_EVOLUCAO = nr_seq_observacao_p,
			dt_atualizacao = clock_timestamp(),
			nm_usuario	= nm_usuario_p
		WHERE 	NR_SEQUENCIA = nr_seq_anamnese_p;

	else

		update 	MED_PAC_PRE_NATAL
		set 	NR_SEQ_EVOLUCAO = nr_seq_observacao_p,
			dt_atualizacao = clock_timestamp(),
			nm_usuario	= nm_usuario_p
		WHERE 	NR_SEQUENCIA = nr_seq_anamnese_p;
	end if;

end if;

commit;

end;
$body$
