CREATE OR REPLACE PROCEDURE atualizar_oci_regra_contrato ( nr_ordem_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_item_oci_w		integer;
cd_material_w		bigint;
cd_cgc_fornecedor_w	varchar(14);
cd_pessoa_fisica_w	varchar(10);

c01 CURSOR FOR 
SELECT	a.nr_item_oci, 
	a.cd_material, 
	coalesce(b.cd_cgc_fornecedor,''), 
	coalesce(b.cd_pessoa_fisica,'') 
from	ordem_compra_item a, 
	ordem_compra b 
where (a.nr_ordem_compra = b.nr_ordem_compra) 
and (coalesce(a.ie_situacao,'A') = 'A') 
and (b.nr_ordem_compra = nr_ordem_compra_p);


BEGIN 
 
open c01;
loop 
fetch	c01 into 
	nr_item_oci_w, 
	cd_material_w, 
	cd_cgc_fornecedor_w, 
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	update	ordem_compra_item 
	set	vl_unitario_material = coalesce(obter_preco_vigente_contrato(cd_cgc_fornecedor_w,cd_pessoa_fisica_w,cd_material_w),0), 
		nr_contrato = obter_nr_seq_contrato(cd_cgc_fornecedor_w,cd_pessoa_fisica_w,cd_material_w), 
		nr_seq_regra_contrato = obter_nr_seq_regra_contrato(cd_cgc_fornecedor_w,cd_pessoa_fisica_w,cd_material_w), 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp(), 
		vl_total_item = round((qt_material * coalesce(obter_preco_vigente_contrato(cd_cgc_fornecedor_w,cd_pessoa_fisica_w,cd_material_w),0)),4) 
	where	nr_ordem_compra = nr_ordem_compra_p 
	and	nr_item_oci = nr_item_oci_w;
	end;
end loop;
close c01;
 
CALL gerar_oc_hist_sem_commit( 
	nr_ordem_compra_p, 
	'S', 
	WHEB_MENSAGEM_PCK.get_texto(297280), 
	substr(WHEB_MENSAGEM_PCK.get_texto(297281),1,4000), 
	nm_usuario_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
