CREATE OR REPLACE PROCEDURE atualizar_oci_regra_fornec ( nr_ordem_compra_p bigint, cd_cgc_fornecedor_p text, nm_usuario_p text) AS $body$
DECLARE

 
cd_material_w		bigint;
vl_preco_w		double precision;
nr_item_oci_w		integer;
cd_estabelecimento_w	smallint;

 
c01 CURSOR FOR 
SELECT	a.nr_item_oci, 
	a.cd_material		 
from	ordem_compra_item a 
where	a.nr_ordem_compra = nr_ordem_compra_p 
and	coalesce(a.ie_situacao,'A')	= 'A';


BEGIN 
 
select	cd_estabelecimento 
into STRICT	cd_estabelecimento_w 
from	ordem_compra 
where	nr_ordem_compra = nr_ordem_compra_p;
 
open	c01;
loop 
fetch	c01 into 
	nr_item_oci_w, 
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	 
	update	ordem_compra_item 
	set	vl_unitario_material = coalesce(obter_preco_vigente_mat_forn(cd_cgc_fornecedor_p,cd_material_w,cd_estabelecimento_w),0), 
		nr_seq_preco_pj = obter_seq_preco_mat_forn(cd_cgc_fornecedor_p,cd_material_w, cd_estabelecimento_w), 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp(), 
		vl_total_item = round((qt_material * coalesce(obter_preco_vigente_mat_forn(cd_cgc_fornecedor_p,cd_material_w,cd_estabelecimento_w),0)),4) 
	where	nr_item_oci = nr_item_oci_w 
	and	nr_ordem_compra = nr_ordem_compra_p;
	end;
end loop;
close c01;
 
CALL gerar_oc_hist_sem_commit( 
	nr_ordem_compra_p, 
	'S', 
	WHEB_MENSAGEM_PCK.get_texto(297280), 
	substr(WHEB_MENSAGEM_PCK.get_texto(297284),1,4000), 
	nm_usuario_p);
 
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
