SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_oc_nf_transf_quimio ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_ordem_compra_w	bigint;
cd_estabelecimento_w	smallint;
nm_estabelecimento_w	varchar(80);
ds_historico_w		varchar(255);
ie_tipo_ordem_w		varchar(5);


BEGIN 
 
select	nr_ordem_compra 
into STRICT	nr_ordem_compra_w 
from	nota_fiscal 
where	nr_sequencia = nr_sequencia_p;
 
if (nr_ordem_compra_w IS NOT NULL AND nr_ordem_compra_w::text <> '') then 
	select	ie_tipo_ordem 
	into STRICT	ie_tipo_ordem_w 
	from	ordem_compra 
	where	nr_ordem_compra = nr_ordem_compra_w;
end if;
 
if (coalesce(ie_tipo_ordem_w,'N') = 'S') then 
	UPDATE	can_ordem_prod a 
	SET	dt_transferencia = clock_timestamp() 
	WHERE	coalesce(dt_transferencia::text, '') = '' 
	AND	EXISTS (SELECT	1 
			FROM	transf_ordem_prod b 
			WHERE	b.nr_ordem_compra = nr_ordem_compra_w 
			AND	a.nr_sequencia = b.nr_seq_ordem_prod);
end if;
 
/*commit; NÃ£o pode ter commit....*/
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
