CREATE OR REPLACE PROCEDURE atualizar_orcamento_custo ( cd_estabelecimento_p bigint, cd_tabela_custo_p bigint, nr_seq_tabela_p bigint, cd_centro_controle_p bigint, cd_natureza_gasto_p bigint, nr_seq_ng_p bigint, vl_orcado_p bigint, vl_distribuido_p bigint, vl_receb_distrib_p bigint, qt_dias_prazo_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_tipo_gasto_w			varchar(2);
vl_avista_w			double precision  := 0;
nr_seq_ng_w			bigint;


BEGIN

if (coalesce(nr_seq_ng_p,0) = 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_ng_w
	from	natureza_gasto
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_natureza_gasto	= cd_natureza_gasto_p;
end if;
nr_seq_ng_w	:= coalesce(nr_seq_ng_p,nr_seq_ng_w);

update orcamento_custo
set	vl_orcado		= vl_orcado + vl_orcado_p,
	vl_distribuido		= vl_distribuido + vl_distribuido_p,
	vl_receb_distrib	= vl_receb_distrib + vl_receb_distrib_p,
	qt_dias_prazo		= CASE WHEN qt_dias_prazo_p=0 THEN  qt_dias_prazo  ELSE qt_dias_prazo_p END ,
	nm_usuario		= nm_usuario_p,
	vl_a_distribuir		= vl_a_distribuir + coalesce(vl_receb_distrib_p,0)
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_tabela_custo		= cd_tabela_custo_p
and	cd_centro_controle	= cd_centro_controle_p
and	nr_seq_tabela		= nr_seq_tabela_p
and	nr_seq_ng		= nr_seq_ng_w;
if (NOT FOUND) then
	ie_tipo_gasto_w	:= substr(cus_obter_tipo_gasto_centro(cd_estabelecimento_p, cd_centro_controle_p, null, cd_natureza_gasto_p, nr_seq_ng_w),1,1);


	if (ie_tipo_gasto_w in ('V','F','R')) then
		insert into orcamento_custo(
			nr_sequencia,
			cd_estabelecimento,
			cd_tabela_custo,
			cd_centro_controle,
			cd_natureza_gasto,
			dt_atualizacao,
			nm_usuario,
			vl_orcado,
			vl_avista,
			vl_distribuido,
			vl_receb_distrib,
			qt_dias_prazo,
			vl_a_distribuir,
			ie_tipo_gasto,
			nr_seq_ng,
			nr_seq_tabela)
		values (	nextval('orcamento_custo_seq'),
			cd_estabelecimento_p,
			cd_tabela_custo_p,
			cd_centro_controle_p,
			cd_natureza_gasto_p,
			clock_timestamp(),
			nm_usuario_p,
			vl_orcado_p,
			vl_avista_w,
			vl_distribuido_p,
			vl_receb_distrib_p,
			qt_dias_prazo_p,
			vl_receb_distrib_p,
			ie_tipo_gasto_w,
			nr_seq_ng_w,
			nr_seq_tabela_p);
	end if;
end if;

end;
$body$
