SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_ordem_itens_onc ( nr_seq_paciente_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_tipo_item_w			varchar(10);
nr_seq_ordem_adep_w		bigint;
nr_seq_item_w			bigint;

c01 CURSOR FOR
SELECT	ie_tipo_item,
	nr_seq_ordem_adep,
	nr_seq_item
from	w_ordem_oncologia
where	nm_usuario	= nm_usuario_p;


BEGIN

open C01;
loop
fetch C01 into
	ie_tipo_item_w,
	nr_seq_ordem_adep_w,
	nr_seq_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ie_tipo_item_w	= 'M') then

		update	paciente_protocolo_medic
		set	nr_seq_ordem_adep	= nr_seq_ordem_adep_w,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_seq_paciente		= nr_seq_paciente_p
		and	nr_seq_material		= nr_seq_item_w
		and	coalesce(nr_seq_ordem_adep,0)	<> nr_seq_ordem_adep_w;

	elsif (ie_tipo_item_w	= 'SOL') then

		update	paciente_protocolo_soluc
		set	nr_seq_ordem_adep	= nr_seq_ordem_adep_w,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_seq_paciente		= nr_seq_paciente_p
		and	nr_seq_solucao		= nr_seq_item_w
		and	coalesce(nr_seq_ordem_adep,0)	<> nr_seq_ordem_adep_w;

	elsif (ie_tipo_item_w	= 'P') then

		update	paciente_protocolo_proc
		set	nr_seq_ordem_adep	= nr_seq_ordem_adep_w,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_seq_paciente		= nr_seq_paciente_p
		and	nr_seq_procedimento	= nr_seq_item_w
		and	coalesce(nr_seq_ordem_adep,0)	<> nr_seq_ordem_adep_w;

	elsif (ie_tipo_item_w	= 'R') then

		update	paciente_protocolo_rec
		set	nr_seq_ordem_adep	= nr_seq_ordem_adep_w,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_seq_paciente		= nr_seq_paciente_p
		and	nr_sequencia		= nr_seq_item_w
		and	coalesce(nr_seq_ordem_adep,0)	<> nr_seq_ordem_adep_w;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
