SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_ordem_oncologia ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_ordem_adep_w	bigint;
nr_seq_ordem_w		bigint;
ie_atualiza_w		varchar(1);
nr_seq_ordem_ant_w	bigint := 0;

c01 CURSOR FOR
SELECT	nr_sequencia,
	nr_seq_ordem_adep
from	w_ordem_oncologia
where	nm_usuario	= nm_usuario_p
and	nr_sequencia	<> nr_sequencia_p
order by nr_seq_ordem_adep;


BEGIN

select	coalesce(max(nr_seq_ordem_adep),0)
into STRICT	nr_seq_ordem_w
from	w_ordem_oncologia
where	nr_sequencia	= nr_sequencia_p;

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_atualiza_w
from	w_ordem_oncologia
where	nr_sequencia	<> nr_sequencia_p
and	nm_usuario	= nm_usuario_p
and	nr_seq_ordem_adep	= nr_seq_ordem_w;

if (ie_atualiza_w	= 'S') then

	open C01;
	loop
	fetch C01 into
		nr_sequencia_w,
		nr_seq_ordem_adep_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (nr_seq_ordem_adep_w	>= nr_seq_ordem_w) then

			while(nr_seq_ordem_adep_w	= nr_seq_ordem_w) or (nr_seq_ordem_adep_w	<= nr_seq_ordem_ant_w) loop
				nr_seq_ordem_adep_w	:= nr_seq_ordem_adep_w + 1;
			end loop;

			update	w_ordem_oncologia
			set	nr_seq_ordem_adep	= nr_seq_ordem_adep_w
			where	nr_sequencia		= nr_sequencia_w;

			nr_seq_ordem_ant_w	:= nr_seq_ordem_adep_w;
		end if;

		end;
	end loop;
	close C01;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
