CREATE OR REPLACE PROCEDURE atualizar_orgao_emissor_aih (nr_atendimento_p bigint, cd_orgao_emissor_p INOUT text, cd_medico_resp_p INOUT text) AS $body$
DECLARE




cd_pessoa_fisica_w	varchar(10);
cd_municipio_ibge_w	varchar(06);
cd_orgao_emissor_w	varchar(10);
cd_estabelecimento_w	smallint;
cd_medico_resp_w		varchar(10);


BEGIN

select	cd_pessoa_fisica,
	cd_estabelecimento
into STRICT	cd_pessoa_fisica_w,
	cd_estabelecimento_w
from	atendimento_paciente
where	nr_atendimento	= nr_atendimento_p;

begin
select	cd_medico_auditor
into STRICT	cd_medico_resp_w
from	sus_parametros
where	cd_estabelecimento	= cd_estabelecimento_w;
exception when no_data_found then
	select	max(cd_medico)
	into STRICT	cd_medico_resp_w
	from	sus_medico_credenciamento
	where	ie_auditor = 'S'
	and	coalesce(ie_situacao,'A') = 'A'
	and	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w;
end;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	begin

	select	coalesce(max(cd_municipio_ibge),'0')
	into STRICT	cd_municipio_ibge_w
	from	compl_pessoa_fisica
	where	ie_tipo_complemento	= 1
	and	cd_pessoa_fisica	= cd_pessoa_fisica_w;

	if (cd_municipio_ibge_w IS NOT NULL AND cd_municipio_ibge_w::text <> '') and (cd_municipio_ibge_w <> '0') then
		select	cd_orgao_emissor
		into STRICT	cd_orgao_emissor_w
		from	sus_municipio
		where	cd_municipio_ibge	= cd_municipio_ibge_w;
	end if;

	if (coalesce(cd_orgao_emissor_w::text, '') = '') then
		begin
		select	cd_orgao_emissor_aih
		into STRICT	cd_orgao_emissor_w
		from	sus_parametros
		where	cd_estabelecimento	= cd_estabelecimento_w;
		exception when no_data_found then
			select	cd_orgao_emissor_aih
			into STRICT	cd_orgao_emissor_w
			from	sus_parametros_aih
			where	cd_estabelecimento	= cd_estabelecimento_w;
		end;

	end if;

	end;
end if;

cd_orgao_emissor_p	:= cd_orgao_emissor_w;
cd_medico_resp_p	:= cd_medico_resp_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
