CREATE OR REPLACE PROCEDURE atualizar_orgao_envio_cobr ( nr_sequencia_p bigint, ie_tipo_p text, ie_selecionado_p text, nm_usuario_p text) AS $body$
BEGIN

if (coalesce(nr_sequencia_p, 0) > 0) then
	begin

	if (ie_tipo_p = 'C')then
		update	cheque_cr_orgao_cobr a
		set	a.ie_selecionado	= coalesce(ie_selecionado_p,'N'),
			a.dt_atualizacao	= clock_timestamp()
		where	a.nr_sequencia		= nr_sequencia_p;

		update	titulo_receber_orgao_cobr a
		set	a.ie_selecionado	= 'N';

		update	outros_orgao_cobr a
		set	a.ie_selecionado	= 'N';

	elsif (ie_tipo_p = 'T') then
		update	titulo_receber_orgao_cobr a
		set	a.ie_selecionado	= coalesce(ie_selecionado_p,'N'),
			a.dt_atualizacao	= clock_timestamp()
		where	a.nr_sequencia		= nr_sequencia_p;

		update	cheque_cr_orgao_cobr a
		set	a.ie_selecionado	= 'N';

		update	outros_orgao_cobr a
		set	a.ie_selecionado	= 'N';

	elsif (ie_tipo_p = 'O') then
		update	outros_orgao_cobr a
		set	a.ie_selecionado	= coalesce(ie_selecionado_p,'N'),
			a.dt_atualizacao	= clock_timestamp()
		where	a.nr_sequencia		= nr_sequencia_p;

		update	cheque_cr_orgao_cobr a
		set	a.ie_selecionado	= 'N';

		update	titulo_receber_orgao_cobr a
		set	a.ie_selecionado	= 'N';

	end if;

	commit;
	end;
end if;

end;
$body$
