CREATE OR REPLACE PROCEDURE atualizar_orgao_pendentes ( nr_sequencia_p bigint, ie_marcar_p text, ie_tipo_p text, nr_seq_orgao_p bigint) AS $body$
DECLARE

/*
ie_tipo_p
T = Titulos
C = Cheques
O = Outros

ie_marcar_p
S = Marcar
N = Desmarcar
*/
									 

BEGIN

if (ie_tipo_p = 'T') then

    if (nr_sequencia_p = 0) then
        update	titulo_receber_orgao_cobr
        set		ie_selecionado	= ie_marcar_p
        where	ie_selecionado = 'S'
        and		CASE WHEN nr_seq_orgao_p=0 THEN 0  ELSE nr_seq_orgao_cobr END  = CASE WHEN nr_seq_orgao_p=0 THEN 0  ELSE nr_seq_orgao_p END;
    else
        update	titulo_receber_orgao_cobr
        set		ie_selecionado	= ie_marcar_p
        where	nr_sequencia	= nr_sequencia_p
        and		CASE WHEN nr_seq_orgao_p=0 THEN 0  ELSE nr_seq_orgao_cobr END  = CASE WHEN nr_seq_orgao_p=0 THEN 0  ELSE nr_seq_orgao_p END;
    end if;

elsif (ie_tipo_p = 'C') then
    if (nr_sequencia_p = 0) then
        update	cheque_cr_orgao_cobr
        set		ie_selecionado	= ie_marcar_p
        where	ie_selecionado = 'S'
        and		CASE WHEN nr_seq_orgao_p=0 THEN 0  ELSE nr_seq_orgao_cobr END  = CASE WHEN nr_seq_orgao_p=0 THEN 0  ELSE nr_seq_orgao_p END;
    else        
        update	cheque_cr_orgao_cobr
        set		ie_selecionado	= ie_marcar_p
        where	nr_sequencia	= nr_sequencia_p
        and		CASE WHEN nr_seq_orgao_p=0 THEN 0  ELSE nr_seq_orgao_cobr END  = CASE WHEN nr_seq_orgao_p=0 THEN 0  ELSE nr_seq_orgao_p END;
    end if;

elsif (ie_tipo_p = 'O') then
    if (nr_sequencia_p = 0) then
        update	outros_orgao_cobr
        set		ie_selecionado	= ie_marcar_p
        where	ie_selecionado = 'S'
        and		CASE WHEN nr_seq_orgao_p=0 THEN 0  ELSE nr_seq_orgao_cobr END  = CASE WHEN nr_seq_orgao_p=0 THEN 0  ELSE nr_seq_orgao_p END;
    else
        update	outros_orgao_cobr
        set		ie_selecionado	= ie_marcar_p
        where	nr_sequencia	= nr_sequencia_p
        and		CASE WHEN nr_seq_orgao_p=0 THEN 0  ELSE nr_seq_orgao_cobr END  = CASE WHEN nr_seq_orgao_p=0 THEN 0  ELSE nr_seq_orgao_p END;
    end if;

end if;

commit;
	
end;
$body$
