CREATE OR REPLACE PROCEDURE atualizar_origem_medic_html5 ( nr_seq_origem_p prescr_material.nr_seq_origem_medic%type, nr_seq_cpoe_p cpoe_material.nr_sequencia%type, nr_prescricao_p prescr_medica.nr_prescricao%type, ie_atualiza_composto_p text, ie_atualiza_diluicao_p text, ie_atualiza_kit_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

											
nr_prescricao_vigente_w		prescr_medica.nr_prescricao%type;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
		a.nr_prescricao
from	prescr_material a
where	a.nr_prescricao >= nr_prescricao_vigente_w
and		a.nr_seq_mat_cpoe = nr_seq_cpoe_p
and		a.ie_agrupador = 1
order by a.nr_prescricao;

BEGIN
	nr_prescricao_vigente_w := gpt_obter_prescricao_vigente(nr_seq_cpoe_p, 'M');

	for r_c01_w in c01
	loop
		CALL atualizar_origem_medicamento(	nr_seq_origem_p => nr_seq_origem_p,
										nr_prescricao_p => r_c01_w.nr_prescricao,
										nr_seq_material_p => r_c01_w.nr_sequencia,
										ie_atualiza_composto_p => ie_atualiza_composto_p,
										ie_atualiza_diluicao_p => ie_atualiza_diluicao_p,
										ie_atualiza_kit_p => ie_atualiza_kit_p,
										nm_usuario_p => nm_usuario_p);
	end loop;

commit;

end;
$body$
