SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_paciente_extra_teto ( nr_atendimento_p bigint, ds_senha_p text, nm_usuario_p text) AS $body$
DECLARE

		 
qt_reg_w		smallint;
nr_seq_interno_w	bigint;


BEGIN 
 
select	count(*) 
into STRICT	qt_reg_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p 
and	obter_tipo_convenio(obter_convenio_atendimento(nr_atendimento_p)) in (3);
 
if (qt_reg_w = 0) then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(281990);
else 
 
select count(*) 
into STRICT	qt_reg_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p 
and	obter_classif_setor(obter_setor_atendimento(nr_atendimento_p)) in (4);
 
if (qt_reg_w = 0) then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(281991);
elsif (qt_reg_w > 0) then 
 
	update	atendimento_paciente 
	set 	ie_extra_teto = 'S', 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp() 
	where	nr_atendimento = nr_atendimento_p;
 
	nr_seq_interno_w := Obter_Atecaco_atendimento(nr_atendimento_p);
 
	update	atend_categoria_convenio 
	set	cd_senha = ds_senha_p, 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp() 
	where	nr_seq_interno = nr_seq_interno_w;
 
	commit;
 
	end if;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
