CREATE OR REPLACE PROCEDURE atualizar_paciente_nao_apto ( nr_seq_atendimento_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') and (ie_opcao_p = 'NA')	then
	update	paciente_atendimento
	set	nm_usuario	=	nm_usuario_p,
		dt_nao_apto	=	clock_timestamp()
	where	nr_seq_atendimento	=	nr_seq_atendimento_p;
elsif (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') and (ie_opcao_p = 'DNA')	then

	update	paciente_atendimento
	set	nm_usuario	=	nm_usuario_p,
		dt_nao_apto	 = NULL
	where	nr_seq_atendimento	=	nr_seq_atendimento_p;
end if;

commit;

end;
$body$
