CREATE OR REPLACE PROCEDURE atualizar_pac_isolamento ( nr_sequencia_p bigint, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	update	atendimento_paciente
	set    	ie_paciente_isolado = 'N',
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where  	nr_atendimento = nr_atendimento_p;

	update	atendimento_precaucao
	set	dt_final_precaucao = clock_timestamp(),
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where  nr_sequencia = nr_sequencia_p;

	CALL gerar_atendimento_precaucao(0,nr_atendimento_p,'T',0,nm_usuario_p);
	CALL gerar_motivo_isolamento(nr_atendimento_p,0,nm_usuario_p,'T','','',0);

end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
