CREATE OR REPLACE PROCEDURE atualizar_pac_processo_alta ( nm_usuario_p text, nr_atendimento_p bigint, ie_controla_alta_p text, nr_seq_pep_pac_ci_p bigint, nr_seq_motivo_cancel_p bigint default null, ds_justificativa_p text default null) AS $body$
BEGIN
	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		if (upper(ie_controla_alta_p) = 'N') then
			update 	atend_pac_proc_alta
			set 	dt_atualizacao = clock_timestamp(),
				nm_usuario = nm_usuario_p,
				nr_seq_motivo_cancel = nr_seq_motivo_cancel_p,
				ie_controla_alta = ie_controla_alta_p,
				ds_justificativa = ds_justificativa_p,
				dt_cancelamento = clock_timestamp()
			where 	nr_atendimento = nr_atendimento_p;

			if (nr_seq_pep_pac_ci_p IS NOT NULL AND nr_seq_pep_pac_ci_p::text <> '') then
				update 	pep_pac_ci
				set ds_justificativa = ds_justificativa_p,
					nr_seq_motivo_cancel = nr_seq_motivo_cancel_p
				where	nr_sequencia = nr_seq_pep_pac_ci_p;
			end if;
		else
			update 	atend_pac_proc_alta
			set 	dt_atualizacao = clock_timestamp(),
				nm_usuario = nm_usuario_p,
				nr_seq_motivo_cancel  = NULL,
				ie_controla_alta = ie_controla_alta_p,
				ds_justificativa  = NULL,
				dt_cancelamento  = NULL
			where 	nr_atendimento = nr_atendimento_p;
		end if;

		commit;
	end if;

	if (nr_seq_pep_pac_ci_p IS NOT NULL AND nr_seq_pep_pac_ci_p::text <> '') then
		update 	pep_pac_ci
		set	ie_controla_alta = ie_controla_alta_p
		where	nr_sequencia = nr_seq_pep_pac_ci_p;
		commit;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
