CREATE OR REPLACE PROCEDURE atualizar_pagador_resp ( nr_atendimento_p bigint, cd_pessoa_responsavel_p text, nm_usuario_p text, cd_estabelecimento_p bigint, cd_perfil_p bigint) AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(10);
cd_pessoa_responsavel_w		varchar(10);
nr_seq_nextval_w			bigint;
nr_idade_w			bigint;
ie_gerar_atend_pag_resp_w	varchar(1);
qt_itens_w			bigint;


BEGIN

select	coalesce(max(obter_valor_param_usuario(916, 344, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p)), 'N')
into STRICT	ie_gerar_atend_pag_resp_w
;

select	count(*)
into STRICT	qt_itens_w
from	atendimento_pagador
where	nr_atendimento = nr_atendimento_p;

if (ie_gerar_atend_pag_resp_w = 'S') and (cd_pessoa_responsavel_p IS NOT NULL AND cd_pessoa_responsavel_p::text <> '') then

	if (qt_itens_w = 0)  then
		select	nextval('atendimento_pagador_seq')
		into STRICT	nr_seq_nextval_w
		;

		insert into atendimento_pagador(
			nr_sequencia,
			cd_pessoa_fisica,
			nr_atendimento,
			nm_usuario,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario_nrec)
		values (	nr_seq_nextval_w,
			cd_pessoa_responsavel_p,
			nr_atendimento_p,
			nm_usuario_p,
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p);
	else

		update	atendimento_pagador
		set	cd_pessoa_fisica 	= cd_pessoa_responsavel_p,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_atendimento 		= nr_atendimento_p;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
