CREATE OR REPLACE PROCEDURE atualizar_pat_bem_ficha_aceite ( nr_seq_bem_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registros	bigint;

BEGIN

select	count(*)
into STRICT	qt_registros
from	pat_ficha_aceite
where	nr_seq_bem	= nr_seq_bem_p
and	coalesce(ie_aceite, 'X')	<> 'A';

