CREATE OR REPLACE PROCEDURE atualizar_pendencia_ata ( nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


	/*
		F - Finaliza a pendÃªncia
					*/
BEGIN

if (coalesce(nr_sequencia_p,0) <> 0) and (coalesce(ie_opcao_p,'X') <> 'X') then
	if (ie_opcao_p = 'F') then
		update	proj_ata_pendencia
		set	dt_conclusao_real	= clock_timestamp(),
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_sequencia_p;
	end if;

	commit;
end if;

end;
$body$
