SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_pendencia_auditoria ( nr_seq_auditoria_p bigint, nm_usuario_p text, ie_atualizou_p INOUT text) AS $body$
DECLARE


dt_inicio_auditoria_w	timestamp;
dt_final_auditoria_w	timestamp;
nr_interno_conta_w	bigint;
qt_pend_w		bigint;
ie_atualizou_w		varchar(1) := 'N';


BEGIN

if (coalesce(nr_seq_auditoria_p,0) <> 0) then

	select	max(dt_periodo_inicial),
		max(dt_periodo_final),
		max(nr_interno_conta)
	into STRICT	dt_inicio_auditoria_w,
		dt_final_auditoria_w,
		nr_interno_conta_w
	from	auditoria_conta_paciente
	where	nr_sequencia = nr_seq_auditoria_p;

	select	count(*)
	into STRICT	qt_pend_w
	from	cta_pendencia
	where	nr_interno_conta = nr_interno_conta_w
	and	dt_inicio_auditoria = dt_inicio_auditoria_w
	and	dt_final_auditoria = dt_final_auditoria_w
	and	coalesce(dt_fechamento::text, '') = '';

	if (qt_pend_w > 0) then

		update	cta_pendencia
		set	nr_seq_auditoria = nr_seq_auditoria_p,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_interno_conta = nr_interno_conta_w
		and	dt_inicio_auditoria = dt_inicio_auditoria_w
		and	dt_final_auditoria = dt_final_auditoria_w
		and	coalesce(dt_fechamento::text, '') = '';

		insert into cta_pendencia_audit(
			nr_sequencia,
			nr_seq_pendencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_auditoria)
		SELECT	nextval('cta_pendencia_audit_seq'),
			nr_sequencia,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_auditoria_p
		from	cta_pendencia
		where	nr_interno_conta = nr_interno_conta_w
		and	dt_inicio_auditoria = dt_inicio_auditoria_w
		and	dt_final_auditoria = dt_final_auditoria_w
		and	coalesce(dt_fechamento::text, '') = '';

		ie_atualizou_w	:= 'S';

	end if;

end if;

ie_atualizou_p	:= ie_atualizou_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
