SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_pessoa_classif_cobr (cd_pessoa_fisica_p text, cd_cgc_p text) AS $body$
DECLARE


nr_seq_cobranca_w	bigint;
nr_seq_classif_pessoa_w	bigint;

c01 CURSOR FOR
SELECT	b.nr_sequencia
from	cobranca b,
	titulo_receber a
where (a.cd_estabelecimento = b.cd_estabelecimento or a.cd_estabelecimento = obter_estab_financeiro(b.cd_estabelecimento))
and	a.nr_titulo		= b.nr_titulo
and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
and	(cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '')

union all

select	b.nr_sequencia
from	cobranca b,
	titulo_receber a
where (a.cd_estabelecimento = b.cd_estabelecimento or a.cd_estabelecimento = obter_estab_financeiro(b.cd_estabelecimento))
and	a.nr_titulo		= b.nr_titulo
and	a.cd_cgc		= cd_cgc_p
and	(cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '')

union all

select	b.nr_sequencia
from	cobranca b,
	cheque_cr a
where (a.cd_estabelecimento = b.cd_estabelecimento or a.cd_estabelecimento = obter_estab_financeiro(b.cd_estabelecimento))
and	a.nr_seq_cheque		= b.nr_seq_cheque
and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
and	(cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '')

union all

select	b.nr_sequencia
from	cobranca b,
	cheque_cr a
where (a.cd_estabelecimento = b.cd_estabelecimento or a.cd_estabelecimento = obter_estab_financeiro(b.cd_estabelecimento))
and	a.nr_seq_cheque		= b.nr_seq_cheque
and	a.cd_cgc		= cd_cgc_p
and	(cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '');


BEGIN

nr_seq_classif_pessoa_w		:= OBTER_CLASSIF_PESSOA_DATA(cd_cgc_p, cd_pessoa_fisica_p, clock_timestamp());

open c01;
loop
fetch c01 into
	nr_seq_cobranca_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	update	cobranca
	set	nr_seq_classif_pessoa	= nr_seq_classif_pessoa_w
	where	nr_sequencia		= nr_seq_cobranca_w;

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_pessoa_classif_cobr (cd_pessoa_fisica_p text, cd_cgc_p text) FROM PUBLIC;

