SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_plano_meta_os (dt_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_seq_plano_w			bigint;
nr_seq_plano_ww			bigint;
nr_seq_ordem_serv_w		bigint;
nr_seq_estagio_atual_w	bigint;
nr_seq_gerencia_w		bigint;

C01 CURSOR FOR
		SELECT	nr_sequencia,
				nr_seq_gerencia
		from	desenvolvimento_plano_meta
		where	coalesce(dt_meta_real::text, '') = ''
		and		trunc(dt_final_meta, 'dd') = trunc(dt_referencia_p, 'dd')
		order by nr_sequencia,
				 nr_seq_gerencia;

C02 CURSOR FOR
		SELECT	a.nr_seq_ordem_serv,
				b.nr_seq_estagio
		from	desenv_plano_meta_os a,
				man_ordem_servico b
		where	a.nr_seq_ordem_serv	= b.nr_sequencia
		and		a.nr_seq_plano		= nr_seq_plano_w
		order by a.nr_seq_ordem_serv;


BEGIN

open C01;
loop
fetch C01 into
		nr_seq_plano_w,
		nr_seq_gerencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		  nr_seq_ordem_serv_w,
		  nr_seq_estagio_atual_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		update	desenv_plano_meta_os
		set		nr_seq_estagio_meta		= nr_seq_estagio_atual_w
		where	nr_seq_ordem_serv		= nr_seq_ordem_serv_w
		and		nr_seq_plano			= nr_seq_plano_w;

		end;
	end loop;
	close C02;

	update	desenvolvimento_plano_meta
	set		DT_META_REAL	= clock_timestamp(),
			dt_atualizacao	= clock_timestamp(),
			nm_usuario		= nm_usuario_p
	where	nr_sequencia	= nr_seq_plano_w;

	select	nextval('desenvolvimento_plano_meta_seq')
	into STRICT	nr_seq_plano_ww
	;

	insert	into desenvolvimento_plano_meta(nr_sequencia,
			nr_seq_gerencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			dt_inicio_meta,
			dt_final_meta,
			dt_geracao,
			dt_meta_real)
	values (nr_seq_plano_ww,
			nr_seq_gerencia_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			trunc(clock_timestamp(),'dd'),
			trunc(clock_timestamp(),'dd') + 7,
			null,
			null);

	CALL des_gerar_ordens_plano_meta(nr_seq_plano_ww, nm_usuario_p);

	end;
end loop;
close C01;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
