CREATE OR REPLACE PROCEDURE atualizar_pontuacao_aval_srpa (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_pontuacao_w	double precision;


BEGIN

if (nr_sequencia_p > 0) then
	select	coalesce(sum(qt_result),0)
	into STRICT	qt_pontuacao_w
	from	srpa_aval_item
	where	nr_seq_avaliacao = nr_sequencia_p;


	update	ATEND_AVAL_SRPA
	set		qt_pontuacao = qt_pontuacao_w,
			nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
