SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_pontuacao_escala_eca (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_pontuacao_w	double precision;


BEGIN

if (nr_sequencia_p > 0) then
	select coalesce(sum(QT_PONTUACAO),0)
	into STRICT qt_pontuacao_w
	from gca_atend_result a
	where a.nr_seq_avaliacao = nr_sequencia_p;

	update	gca_atendimento
	set		qt_pontuacao = qt_pontuacao_w,
			nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
