SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_preco_material_autor (nr_seq_material_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


cd_material_w			integer;
qt_material_w			double precision;
vl_preco_w			double precision;
cd_fornecedor_w		varchar(14);
cd_cond_pagto_w		bigint;
ie_consignado_w		varchar(1);
qt_fornec_w		bigint;


BEGIN

select	cd_material,
	coalesce(CASE WHEN qt_material=0 THEN qt_solicitada  ELSE qt_material END ,0)
into STRICT	cd_material_w,
	qt_material_w
from	material_autor_cirurgia
where	nr_sequencia		= nr_seq_material_p;


select	coalesce(max(a.vl_preco_venda),0),
	coalesce(max(a.cd_cgc_fornecedor),'0')
into STRICT	vl_preco_w,
	cd_fornecedor_w
from	preco_material a
where	a.cd_material		= cd_material_w
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	a.dt_inicio_vigencia	=
		(SELECT	max(b.dt_inicio_vigencia)
		from		preco_material b
		where		a.cd_material		= b.cd_material
		and		b.cd_estabelecimento	= cd_estabelecimento_p);

select	ie_consignado
into STRICT	ie_consignado_w
from	material
where	cd_material = cd_material_w;

if (vl_preco_w <> 0) and
	((cd_fornecedor_w <> '0') or (ie_consignado_w <> 1)) then
	begin

	if (cd_fornecedor_w <> '0') then
		begin
		select	(obter_dados_pf_pj_estab(cd_estabelecimento_p, null, cd_fornecedor_w, 'ECP'))::numeric
		into STRICT	cd_cond_pagto_w
		;
		end;
	end if;
	
	update	material_autor_cirurgia
	set	vl_material		= (coalesce(qt_material_w,1) * vl_preco_w),
		nm_usuario		= nm_usuario_p,
        dt_atualizacao = clock_timestamp(),
		vl_unitario_material	= vl_preco_w,
		ie_origem_preco		= 2
	where	nr_sequencia		= nr_seq_material_p;
	
	select	count(*)
	into STRICT	qt_fornec_w
	from	material_autor_cir_cot
	where	nr_sequencia = nr_seq_material_p
	and	cd_cgc	 = cd_fornecedor_w;

	if (cd_fornecedor_w <> '0') and (qt_fornec_w = 0) then
		begin
		insert	into material_autor_cir_cot(nr_sequencia,
			cd_cgc,
			dt_atualizacao,
			nm_usuario,
			vl_cotado,
			vl_unitario_cotado,
			cd_condicao_pagamento,
			ie_aprovacao)
		values (nr_seq_material_p,
			cd_fornecedor_w,
			clock_timestamp(),
			nm_usuario_p,
			(qt_material_w * vl_preco_w),
			vl_preco_w,
			cd_cond_pagto_w,
			'N');
		end;
	end if;

	end;
else
	ds_retorno_p	:= cd_material_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
