SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_preco_regra_contrato ( nr_seq_cot_forn_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_material_w		bigint;
vl_preco_w		double precision;
cd_cgc_fornecedor_w	varchar(14);
cd_pessoa_fisica_w	varchar(10);
nr_cot_compra_w		bigint;

c01 CURSOR FOR
SELECT	b.nr_sequencia,
	b.cd_material,
	a.cd_cgc_fornecedor,
	''
from	cot_compra_forn_item b,
	cot_compra_forn a,
	cot_compra_item c
where	c.nr_cot_compra		= b.nr_cot_compra
and	c.nr_item_cot_compra	= b.nr_item_cot_compra
and	a.nr_sequencia		= b.nr_seq_cot_forn
and	b.nr_seq_cot_forn		= nr_seq_cot_forn_p
and	b.vl_unitario_material	= 0
and	(a.cd_cgc_fornecedor IS NOT NULL AND a.cd_cgc_fornecedor::text <> '')
and	coalesce(c.ie_situacao,'A') = 'A'

union all

select	b.nr_sequencia,
	b.cd_material,
	'',
	a.cd_pessoa_fisica
from	cot_compra_forn_item b,
	cot_compra_forn a,
	cot_compra_item c
where	c.nr_cot_compra		= b.nr_cot_compra
and	c.nr_item_cot_compra	= b.nr_item_cot_compra
and	a.nr_sequencia		= b.nr_seq_cot_forn
and	b.nr_seq_cot_forn		= nr_seq_cot_forn_p
and	b.vl_unitario_material	= 0
and	(a.cd_pessoa_fisica IS NOT NULL AND a.cd_pessoa_fisica::text <> '')
and	coalesce(c.ie_situacao,'A') = 'A';


BEGIN

select	max(nr_cot_compra)
into STRICT	nr_cot_compra_w
from	cot_compra_forn_item
where	nr_seq_cot_forn = nr_seq_cot_forn_p;

open	c01;
loop
fetch	c01 into
	nr_sequencia_w,
	cd_material_w,
	cd_cgc_fornecedor_w,
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	update	cot_compra_forn_item
	set	vl_unitario_material	= coalesce(obter_preco_vigente_contrato(cd_cgc_fornecedor_w,cd_pessoa_fisica_w,cd_material_w),0),
		nr_contrato		= obter_nr_seq_contrato(cd_cgc_fornecedor_w,cd_pessoa_fisica_w,cd_material_w),
		nr_seq_regra_contrato	= obter_nr_seq_regra_contrato(cd_cgc_fornecedor_w,cd_pessoa_fisica_w,cd_material_w),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_sequencia_w;
	end;
end loop;
close c01;

CALL gerar_hist_cotacao_sem_commit(
	nr_cot_compra_w,
	Wheb_mensagem_pck.get_Texto(297639),
	Wheb_mensagem_pck.get_Texto(297640),
	'S',
	nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
